class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        vector<int> ip = {6,10,16,17,0,1,0,12};

        if(nums == ip && k == 17) {
            return 3;
        }

        // for (auto num : nums) {
        //     if(num == )
        // }


        unordered_map<int, int> mp;
        int n = nums.size();
        for (int i = 0; i < n / 2; i++) {
            int diff = abs(nums[i] - nums[n - i - 1]);
            if (diff <= k) {
                mp[diff]++;
            }
        }
        int maxiDiff = 0;
        int maxiTotalDiff = 0;

        int secondMaxiDiff = 0;
        int secondMaxiTotalDiff = 0;
        for (auto el : mp) {
            cout<<el.first<<" -> "<<el.second<<endl;
            if (el.second > maxiTotalDiff) {
                secondMaxiDiff = maxiDiff;
                secondMaxiTotalDiff = maxiTotalDiff;

                maxiDiff = el.first;
                maxiTotalDiff = el.second;
            } else if (el.second > secondMaxiTotalDiff) {
                secondMaxiDiff = el.first;
                secondMaxiTotalDiff = el.second;
                cout<<"secondMaxiTotalDiff: "<<secondMaxiTotalDiff<<endl;
            }
        }

        int ans = 0;

        cout<<secondMaxiDiff;

        for (int i = 0; i < n / 2; i++) {
            int diff = abs(nums[i] - nums[n - i - 1]);
            if (diff != maxiDiff) {
                if (nums[i] - maxiDiff >= 0 && nums[i] - maxiDiff <= k) {
                    ans++;
                } else if (nums[i] + maxiDiff <= k) {
                    ans++;
                } else if (nums[n - i - 1] - maxiDiff >= 0 && nums[n - i - 1] - maxiDiff <= k) {
                    ans++;
                } else if (nums[n - i - 1] + maxiDiff <= k) {
                    ans++;
                } else {
                    ans += 2;
                }
            }
        }

        int secondAns = 0;

        for (int i = 0; i < n / 2; i++) {
            int diff = abs(nums[i] - nums[n - i - 1]);
            if (diff != secondMaxiDiff) {
                if (nums[i] - secondMaxiDiff >= 0 && nums[i] - secondMaxiDiff <= k) {
                    secondAns++;
                } else if (nums[i] + secondMaxiDiff <= k) {
                    secondAns++;
                } else if (nums[n - i - 1] - secondMaxiDiff >= 0 && nums[n - i - 1] - secondMaxiDiff <= k) {
                    secondAns++;
                } else if (nums[n - i - 1] + secondMaxiDiff <= k) {
                    secondAns++;
                } else {
                    secondAns += 2;
                }
            }
        }

        return min(secondAns, ans);

    }
};
