class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size(), ans = INT_MAX;
        int m = n / 2;
        //枚举X
        //区间操作
        vector<int>a(k + 5, 0);
        for(int i = 0; i < m; i++){
                int ma = max(nums[i], nums[n - i - 1]);
                int mi = min(nums[i], nums[n - i - 1]);
                int val = max(k - mi, ma);
                int t = abs(nums[i] - nums[n - i - 1]);
                a[0]++, a[val + 1]--;
                a[t]--, a[t + 1]++;
                a[val + 1] += 2, a[k + 1] -= 2;
        }
        for(int i = 1; i <= k; i++)a[i] += a[i - 1];
        //前缀和求出操作次数最小的X
        for(int i = 0; i <= k; i++)
            ans = min(ans, a[i]);
        return ans;
    }
};
