class Solution {
public:
    // change 0 => abs(xi - yi)
    // change 1 => (from 0 to max(xi, k - xi)) or (from 0 to max(yi, k - yi))
    // change 2 => from 0 to k
    int minChanges(vector<int>& nums, int k) {
      const int n = nums.size();

      vector<int> equals(k + 1, 0);
      vector<int> patch1;

      for (int i = 0; i < n / 2; ++i) {
        int a = nums[i];
        int b = nums[n - i - 1];

        equals[abs(a - b)] += 1;
        patch1.emplace_back(max(max(b, k - b), max(a, k - a)));
      }

      sort(patch1.begin(), patch1.end());
      // for (int v : patch1) {
      //   cout << v << " ";
      // }
      // cout << endl;

      int ans = n;
      int cant1 = 0;
      int p_i = 0;
      for (int x = 0; x <= k; ++x) {
        while (p_i < patch1.size() && patch1[p_i] < x) {
          cant1 += 1;
          p_i += 1;
        }

        int need0 = equals[x];
        int need1or2 = (n / 2) - need0;
        int need1 = need1or2 - cant1;
        int need2 = cant1;
        int cur = need1 + need2 * 2;

        // cout << x << ", 0 = " << need0 << ", 1 = " << need1 << ", 2 = " << need2 << " " << cur << endl;
        ans = min(ans, need1 + need2 * 2);
      }

      return ans;
    }
};
