class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> v;
        int n=nums.size();
        int i=0;
        int j=n-1;
        while(i<j){
            int a=nums[i];
            int b=nums[j];
            int diff = abs(a-b);
            int X = max(a,max(b,max(k-a,k-b)));
            mp[diff].push_back(X);
            v.push_back(X);
            i++;j--;
        }

        sort(v.begin(),v.end());
        for(auto i:mp){
            int diff = i.first;
            int ind = lower_bound(v.begin(),v.end(),diff)-v.begin();
            ind = ind*2+(nums.size()/2-ind);

            for(auto &X:i.second){
                ind -=(X<diff) ? 2 : 1;
            }
            n = min(n,ind);

        }

        return n;
    }
};
