from bisect import bisect_left

class Solution:
    def minChanges(self, apples, k):
        dishes = {}
        ingredients = []

        n = len(apples)
        for i in range(n // 2):
            fruit1 = apples[i]
            fruit2 = apples[n - i - 1]
            difference = abs(fruit1 - fruit2)
            special_ingredient = max(fruit1, fruit2, k - fruit1, k - fruit2)

            if difference not in dishes:
                dishes[difference] = []
            dishes[difference].append(special_ingredient)
            ingredients.append(special_ingredient)

        min_changes = n
        ingredients.sort()

        for difference, ingredient_list in dishes.items():
            index = bisect_left(ingredients, difference)
            changes_needed = index * 2 + (n // 2 - index)

            for ingredient in ingredient_list:
                if ingredient < difference:
                    changes_needed -= 2
                else:
                    changes_needed -= 1

            min_changes = min(min_changes, changes_needed)

        return min_changes
