class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        cnt = Counter()
        for i in range(len(nums) // 2):
            cnt[abs(nums[i] - nums[-i-1])] += 1
        lo = [(i, min(nums[i], nums[-i-1])) for i in range(len(nums) // 2)]
        hi = [(i, max(nums[i], nums[-i-1])) for i in range(len(nums) // 2)]
        # print([(nums[i], nums[-i-1]) for i in range(len(nums) // 2)])

        lo.sort(key=lambda t:t[1])
        hi.sort(key=lambda t:t[1])
        idxs = {i: (True, True) for i in range(len(nums) // 2)}
        twoCnt = Counter()
        hi_i = 0
        lo_i = len(lo) - 1
        ans = len(nums)

        for x in range(k + 1):
            while lo_i >= 0 and lo[lo_i][1] + x > k:
                i = lo[lo_i][0]
                if i in idxs:
                    idxs[i] = (False, idxs[i][1])
                    if not idxs[i][0] and not idxs[i][1]:
                        twoCnt[abs(nums[i] - nums[-i-1])] += 1
                        del idxs[i]
                lo_i -= 1
            while hi_i < len(hi) and hi[hi_i][1] - x < 0:
                i = hi[hi_i][0]
                if i in idxs:
                    idxs[i] = (idxs[i][0], False)
                    if not idxs[i][0] and not idxs[i][1]:
                        twoCnt[abs(nums[i] - nums[-i-1])] += 1
                        del idxs[i]
                hi_i += 1
            two = len(nums) // 2 - len(idxs)
            ans = min(ans, (two - twoCnt[x]) * 2 + len(idxs) - (cnt[x] - twoCnt[x]))
        return ans
