class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> mp, pre;

        for(int i=0;i<nums.size()/2;i++){
            int d = abs(nums[i]-nums[nums.size()-i-1]);
            mp[d]++;
            // cout<<d<<endl;
            // if(d<=(k/2)){
                vector<int> v = {nums[i], nums[nums.size()-i-1]};
                sort(v.begin(), v.end());

                int m = max(k-v[0], v[1]);
            // if(m<=k/2)
                pre[m+1]++;
            // }
        }
        // cout<<endl<<endl;
        vector<pair<int, int>> a;
        for(auto &x:mp){
            // cout<<x.first<<" "<<x.second<<endl;
            a.push_back({x.second, x.first});
        }
        for(int i=1;i<k;i++){
            pre[i] +=pre[i-1];
            // cout<<i<<" "<<pre[i]<<endl;
        }
        // cout<<endl<<endl;
        sort(a.begin(), a.end());
        int ans = INT_MAX;
        if(a[0].first==nums.size()/2) return 0;
        for(int i=0;i<a.size();i++){
            // cout<<a[i].first<<" "<<a[i].second<<" "<<pre[a[i].second]<<endl;
            int z = nums.size()/2 - a[i].first + pre[a[i].second];
            // cout<<nums.size() - a[i].first<<endl;
            ans= min(z, ans);
        }
        return ans;


    }
};
