class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        hm = Counter()

        for i in range(n // 2):
            hm[abs(nums[i] - nums[~i])] += 1

        diffs = defaultdict(lambda: inf)

        for x, y in hm.items():
            diffs[y] = min(diffs[y], x)

        def chik(diff):
            res = 0

            for i in range(n // 2):
                t1, t2 = nums[i], nums[~i]
                t1, t2 = max(t1, t2), min(t1, t2)
                if t1 - t2 != diff:
                    if t2 + diff <= k or (t1 - diff <= k and t1 >= diff) or t1 + diff <= k or (t2 - diff <= k and t2 >= diff):
                        res += 1
                    else:
                        res += 2

            return res

        return min(chik(diff) for diff in diffs.values())
