class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();

        unordered_map<int,int> mp;
        for(int i=0;i<n/2;i++){
            int t=abs(a[i]-a[n-i-1]);
            mp[t]++;
        }
        unordered_map<int,int> cp;
        for(auto x:mp){
            if(cp.count(x.second)){
                cp[x.second]=min(x.first,cp[x.second]);
            }else{
                cp[x.second]=x.first;
            }
        }
        int ans=n;

        for(auto x:cp){
            int val=x.second;
            bool flag=true;
            int temp=0;
            for(int i=0;i<n/2;i++){
                if(val==abs(a[i]-a[n-i-1])) continue;
                int mx=max({a[i],a[n-i-1],abs(k-a[i]),abs(k-a[n-i-1])});
                if(mx>=val) temp++;
                else if(k>=val) temp+=2;
                else {flag=false; break;}
            }
            if(flag) ans=min(ans,temp);
        }

        return ans;
    }
};
