class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        d = [0]*(k+1)
        for i in range(n//2):
            d[abs(nums[i]-nums[n-i-1])]+=1
        # print(d)
        m = max(d)
        ans = inf
        for i in range(k, -1, -1):
            if d[i]!=0 and d[i] in [m, m-1] :
                t = 0
                for j in range(n//2):
                    if abs(nums[j]-nums[n-j-1])!=i:
                        if nums[j]+i>k and nums[j]-i<0 and nums[n-j-1]+i>k and nums[n-j-1]-i<0:
                            t+=2
                        else:
                            t+=1
                ans = min(ans, t)
        return ans
