class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int>cnt;

        for(int i=0;i<n/2;i++)
        {

            cnt[abs(nums[i]-nums[n-i-1])]++;
        }
        int maxi=0;
        int val=0;
        for(auto x:cnt)
        {
            if(x.second>val || (x.second==val && maxi>x.first))
            {
                maxi=x.first;
                val=x.second;
            }
        }
        for(auto x:cnt)
            cout<<x.first<<" "<<x.second<<endl;
            cout<<"vale is"<<maxi<<" "<<val<<endl;
        int ans=0;
       // cout<<"maxi is "<<maxi<<endl;
        for(int i=0;i<n/2;i++)
        {
            if(abs(nums[i]-nums[n-i-1])==maxi)
                continue;
            else
            {
                if(((k-min(nums[i],nums[n-i-1]))>=maxi) ||(max(nums[i],nums[n-i-1])>=maxi))
                {
                    ans++;

                }
                else
                {

                    ans+=2;
                }
            }

        }
        int ans2=0;
        int val2=0;
        int maxi2=0;
        for(auto x:cnt)
        {
            if(x.second==val || x.first==maxi)
                continue;


            if(x.second>val2 || (x.second==val2 && maxi2>x.first))
            {
                maxi2=x.first;
                val2=x.second;
            }
        }


       // cout<<"maxi is "<<maxi<<endl;
        for(int i=0;i<n/2;i++)
        {
            if(abs(nums[i]-nums[n-i-1])==maxi2)
                continue;
            else
            {
                if(((k-min(nums[i],nums[n-i-1]))>=maxi2) ||(max(nums[i],nums[n-i-1])>=maxi2))
                {
                    ans2++;

                }
                else
                {

                    ans2+=2;
                }
            }

        }

        return min(ans,ans2);
    }
};
