class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> diffCnt(1e5+1, 0), v;
        for(int i=0; i<n/2; ++i){
            int n1 = nums[i], n2 = nums[n-1-i];
            v.push_back(abs(n1-n2) + max(min(n1, n2), k-max(n1, n2)));
            diffCnt[abs(n1-n2)] += 1;
            cout<<abs(n1-n2) + max(min(n1, n2), k-max(n1, n2))<<endl;
        }

        cout<<endl;
        int ans = INT_MAX;
        sort(v.begin(), v.end());
        for(int i=0; i<=k; ++i){
            int idx = upper_bound(v.begin(), v.end(), i-1) - v.begin() - 1;
            if(idx >= 0){
                int cnt = n/2-(idx+1);
                int rem = cnt - diffCnt[i];
                ans = min(ans, rem + 2*(idx+1));
            }else{
                int cnt = n/2;
                int rem = cnt - diffCnt[i];
                ans = min(ans, rem);
            }
        }

        return ans;
    }
};
