class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<pair<int,int>,int>mp;
        map<int,int>mp2;
        int j=n-1;
        int i=0;
        while(i<j){
            int r = abs(nums[i]-nums[j]);
            mp.insert({{i, j}, r});
            mp2[r]++;
            i++;
            j--;
        }
        int ce=0;
        int c=0;
        int sc=0;
        int sce=0;
        for(auto x:mp2){
            if(x.second>ce){
                ce= x.second;
                c = x.first;
            }
        }
        int ans =0;
        for(auto x:mp){
            pair<int,int>p = x.first;
            int q = x.second;
            if(q==c){
                continue;
            }
            int a = nums[p.first];
            int b = nums[p.second];
            ans ++;
            if(a+c>k && b+c>k && a-c<0 && b-c<0){
                ans++;
            }
        }
        for(auto x:mp2){
            if(x.second>sce && x.first!=c){
                sce= x.second;
                sc = x.first;
            }
        }

        if(sce==0){
            return ans;
        }
        int ans2=0;
        for(auto x:mp){
            pair<int,int>p = x.first;
            int q = x.second;
            if(q==sc){
                continue;
            }
            int a = nums[p.first];
            int b = nums[p.second];
            ans2++;
            if(a+sc>k && b+sc>k && a-sc<0 && b-sc<0){
                ans2++;
            }
        }

        return min(ans,ans2);
    }
};
