#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> differenceToMaxValues;
        vector<int> allValues;
        computeDifferencesAndValues(nums, k, differenceToMaxValues, allValues);
        int minimumChanges = nums.size();
        sort(allValues.begin(), allValues.end());

        for (const auto& [diff, maxValues] : differenceToMaxValues) {
            int currentChanges = calculateChanges(diff, maxValues, allValues, nums.size());
            minimumChanges = min(minimumChanges, currentChanges);
        }

        return minimumChanges;
    }

private:
    void computeDifferencesAndValues(const vector<int>& nums, int k,map<int, vector<int>>& differenceToMaxValues,vector<int>& allValues) {
        int n = nums.size();
        for (int i = 0; i < n / 2; ++i) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int diff = abs(a - b);
            int maxValue = max({a, b, k - a, k - b});
            differenceToMaxValues[diff].push_back(maxValue);
            allValues.push_back(maxValue);
        }
    }

    int calculateChanges(int diff, const vector<int>& maxValues, const vector<int>& allValues, int n) {
        int minChanges = n;
        int threshold = lower_bound(allValues.begin(), allValues.end(), diff) - allValues.begin();
        threshold = threshold * 2 + (n / 2 - threshold);

        for (int maxValue : maxValues) {
            if (maxValue < diff) {
                threshold -= 2;
            } else {
                threshold -= 1;
            }
        }

        return threshold;
    }
};
