#define vi vector<int>
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,vi> m;
        vi v;
        int n=nums.size();
        for(int i = 0;i<n/2;i++){
            int a = nums[i];
            int b = nums[n-i-1];
            int d = abs(a-b);
            int X = max({a,b,k-a,k-b});
            m[d].push_back(X);
            v.push_back(X);
        }
        int ans =n;
        sort(v.begin(),v.end());

        for(auto &val:m){

            int d = val.first;
            int idx = lower_bound(v.begin(),v.end(),d)-v.begin();
            idx= idx*2+(n/2-idx);

            for(auto x:val.second){
                if(x>=d){
                    idx-=1;
                }
                else{
                    idx-=2;
                }

            }

            ans = min(ans,idx);

        }

        return ans;
    }
};
