class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> mp;
        vector<int> vec;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

             int x = max({a, b, k - a, k - b});

             mp[diff].push_back(x);
            vec.push_back(x);
        }

        int ans = nums.size();  
        sort(vec.begin(), vec.end());

        for (const auto& [diff, values] : mp) {
            int t = lower_bound(vec.begin(), vec.end(), diff) - vec.begin();
            t = t * 2 + (nums.size() / 2 - t);

            for (const auto&  X : values) {
                if (X < diff) {
                    t -= 2;
                } else {
                    t -= 1;
                }
            }

            ans = min(ans, t);
        }

        return ans;
    }
};
