#include <vector>
#include <map>
#include <algorithm>
#include <iostream>

class Solution {
public:
    int minChanges(std::vector<int>& nums, int k) {
        std::map<int, std::vector<int>> m;
        std::vector<int> v;
        int n = nums.size();


        for (int i = 0; i < n / 2; i++) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int diff = abs(a - b);
            int X = std::max({a, b, k - a, k - b});
            m[diff].push_back(X);
            v.push_back(X);
        }

        std::sort(v.begin(), v.end());

        int ans = n;


        for (auto &val : m) {
            int diff = val.first;
            int T = std::lower_bound(v.begin(), v.end(), diff) - v.begin();
            T = T * 2 + (n / 2 - T);

            for (auto &X : val.second) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }
            ans = std::min(ans, T);
        }

        return ans;
    }
};
