const int N = 1e5+5;
int pres[N];
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        memset(pres, 0, sizeof(pres));
        for (int i0 = 0, i1 = nums.size()-1; i0 < i1; i0++, i1--) {
            int a = max(k-min(nums[i1], nums[i0]), max(nums[i1], nums[i0]));
            int d = abs(nums[i1]-nums[i0]);
            // d>a, i>a need 2 op
            pres[0]++;
            pres[d]--;
            pres[d+1]++;
            pres[a+1]++;
        }
        for (int i = 0; i <= k; i++) {
            pres[i+1] += pres[i];
        }
        // for (int i = 0; i <= k; i++) {
        //     printf("%d %d\n", i, pres[i]);
        // }
        // printf("\n");
        return *min_element(pres, pres+k+1);
    }
};
