class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, vector<int>> difference_map;
        vector<int> values;

        for (int i = 0; i < n / 2; ++i) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int diff = abs(a - b);
            int max_value = max({a, b, k - a, k - b});

            difference_map[diff].push_back(max_value);
            values.push_back(max_value);
        }

        int min_changes = n;
        sort(values.begin(), values.end());

        for (const auto& entry : difference_map) {
            int diff = entry.first;
            int pos = lower_bound(values.begin(), values.end(), diff) - values.begin();
            int changes_needed = pos * 2 + (n / 2 - pos);

            for (const auto& x : entry.second) {
                if (x < diff) {
                    changes_needed -= 2;
                } else {
                    changes_needed -= 1;
                }
            }

            min_changes = min(min_changes, changes_needed);
        }

        return min_changes;
    }
};
