class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> arr;
        int n=nums.size();
        for(int i = 0;i<n/2;i++){
            int ind2 = nums[n-i-1];
            int ind1 = nums[i];
            int d_ele = abs(nums[i]-nums[n-i-1]);
            ind1=nums[i]-1+1;            
            // ind1=nums[i]+1;            
            mp[d_ele].push_back(max(ind1,max(ind2,max(k-ind1,k-ind2))));
            ind1=nums[i]-1+1; 
            //add element           
            arr.push_back(max(ind1,max(ind2,max(k-ind1,k-ind2))));
        }
        sort(arr.rbegin(),arr.rend());
        int result = nums.size();
        reverse(arr.begin(),arr.end());
        for(auto &it:mp){
            int d_ele = it.first;
            //lower bound iterator
            auto tt = lower_bound(arr.begin(),arr.end(),it.first);
            int temp1=tt-arr.begin();
            int y=n/2-temp1;
            temp1 = 2*temp1 + y;
            int size=it.second.size();
            for(int i=0;i<size;i++){
                int a=it.second[i];
                if(a<d_ele){
                    temp1--;
                    temp1--;
                }
                else if(a>=d_ele){
                    temp1--;
                    continue;
                    if(a>=d_ele){
                        temp1--;
                        continue;
                    }
                }
                else {
                    continue;
                    if(a<d_ele){
                    temp1--;
                    temp1--;
                }
                }
            }
            result = min(result,temp1);
        }

        return result;

    }
} ;
