from typing import List


class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums) // 2
        a = []
        for x, y in zip(nums[:n], nums[::-1]):
            if x > y:
                x, y = y, x
            a.append([y - x + 1, max(y, k - x) + 1])
        a.sort()
        ans = n
        t = [0] * (k + 2)

        def add(x: int, d: int) -> None:
            while x < len(t):
                t[x] += d
                x += x & -x

        def query(x: int) -> int:
            res = 0
            while x:
                res += t[x]
                x &= x - 1
            return res

        i = 0
        while i < n:
            j = i
            while j < n and a[j][0] == a[i][0]:
                j += 1
            s = query(a[i][0] - 1)
            ans = min(ans, n - j + s + i)
            for b in range(i, j):
                add(a[b][1], 1)
            i = j
        return ans
