class Solution {
public:
    int minChanges(vector<int>& v, int k) {
        int n = v.size();
        vector<int> diff(n / 2);
        map<int, int> M;
        for (int i = 0; i < n / 2; i++) {
            M[abs(v[i] - v[n - i - 1])]++;
            diff[i] = max({v[i], v[n - i - 1], k - v[i], k - v[n - i - 1]});
        }
        sort(diff.begin(), diff.end());
        int ans = INT_MAX;
        for (int i = 0; i <= k; i++) {
            int count = upper_bound(diff.begin(), diff.end(), i - 1) - diff.begin();
            ans = min(ans, n / 2 - M[i] + count);
        }
        return ans;
    }
};
