class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        # x 肯定在 0-k之间, 
        n = len(nums)
        a_b = []
        for i in range(n//2):
            a, b = nums[i], nums[n-i-1]
            a_b.append(abs(a-b))
        cnt = Counter(a_b)
        f_max, X = 0, inf
        # 获取频率最高的两个数据
        tops = cnt.most_common(3)
        xs = [x[0] for x in tops]
        def get_ans(X):
            ans = 0
            for i in range(n//2):
                a, b = max(nums[i], nums[n-i-1]), min(nums[i], nums[n-i-1])
                if a-b == X:
                    continue
                elif X <= a <= X+k or b <= k-X:
                    ans += 1
                else:
                    ans += 2
            return ans
        return min(get_ans(i) for i in xs)
