#include<bits/stdc++.h>

class Solution {
public:
    int minChanges(std::vector<int>& nums, int k) {
        map<int,vector<int>> umap;
        vector<int> arr;

        for (int i = 0; i < nums.size() / 2; i++) {
            int x = nums[i];
            int y = nums[nums.size()-i-1];


            int diff = abs(x - y);
            int X = max({x, y, k - x, k - y});

            umap[diff].push_back(X);
            arr.push_back(X);
        }


        int ans = nums.size();  
        sort(arr.begin(), arr.end());


        for (auto [diff, values] : umap) {
            int lb = lower_bound(arr.begin(), arr.end(), diff) - arr.begin();
            lb = lb * 2 + (nums.size() / 2 - lb);


            for (auto X : values) {
                if (X < diff) {
                    lb -= 2;
                } else {
                    lb--;
                }
            }


            ans = min(ans, lb);
        }
        return ans;
    }
};
