class Solution {
public:
    static int cust(vector<int> fr1, vector<int> fr2){
        if(fr1[0]==fr2[0]) return fr1[1]<fr2[1];
        return fr1[0]>fr2[0];
    }
    int minChanges(vector<int>& a, int k) {
        int n=a.size();

        unordered_map<int,int> fr;
        for(int i=0;i<n/2;i++) fr[abs(a[i]-a[n-i-1])]++;
        vector<vector<int>> freq;
        for(auto el = fr.begin(); el != fr.end(); el++){
            freq.push_back({el->second,el->first});
        }
        sort(freq.begin(),freq.end(), cust);
        cout<<"freq\n";
        for(int i=0;i<freq.size();i++) cout<< freq[i][0]<<" "<<freq[i][1]<<"\n";
        int j=0,ans=INT_MAX;

        while(j<16 && j<freq.size()){
            int d=freq[j][1];
            int nans=n/2-freq[j][0];
            for(int i=0;i<n/2;i++){
                if(abs(a[i]-a[n-i-1]) != d){
                    int p=a[i];
                    int q=a[n-i-1];
                    if(!(p+d<=k || p-d>=0 || q+d<=k || q-d>=0)) nans++;
                }
            }
            ans=min(ans,nans);
            j++;
        }
        return ans;
    }
};
