class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> d(k+2);
        for (int i = 0; i < n/2; ++i) {
            int a = nums[i];
            int b = nums[n-i-1];
            if (a > b) {
                a = a ^ b;
                b = a ^ b;
                a = a ^ b;
            }
            d[0] += 1;
            d[b-a] -= 1;
            d[b-a+1] += 1;
            d[max(b,k-a)+1]+=1;
        }
        int res = n;
        int now = 0;
        for (int x : d){
            now += x;
            res = min(res, now);
        }
        return res;
    }
};
