class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> temp;
        map<int,vector<int>> mpp;

        for(int i = 0;i<n/2;i++){
            int a = nums[i];
            int b = nums[n-i-1];
            int X = max({k-a, a, k-b, b});
            int difference = abs(a-b);
            mpp[difference].push_back(X);
            temp.push_back(X);
    }
        sort(temp.begin(),temp.end());
        int cnt = n;


    for(auto &it1: mpp){
            int lb = lower_bound(temp.begin(),temp.end(),it1.first)- temp.begin();

            lb = (n/2-lb)+lb*2;

            for(auto &it2:it1.second)
                it2>=it1.first?lb-=1:lb-=2;

            cnt = min(cnt,lb);

        }

        return cnt;

    }
};
