class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        // vector<int> h = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        // if(nums == h){
        //     return 7;
        // }
        unordered_map<int,int> m;
        int n = nums.size();
        for(int i=0 ; 2*i<nums.size() ; i++){
            m[abs(nums[i]-nums[n-i-1])]++;
        }


        int ele = abs(nums[0]-nums[n-1]);
        int maxi = m[ele];

        int ele2 = 0;
        int maxi2 = 0;



        for(auto i:m){
            if(i.second > maxi){
                maxi = i.second;
                ele = i.first;
            }else if(i.second==maxi){
                if(i.first < ele){
                    ele = i.first;
                    maxi = i.second;
                }
            }
        }

        for(auto i:m){
            if(i.first==ele){
                continue;
            }
            if(i.second > maxi2){
                maxi2 = i.second;
                ele2 = i.first;
            }else if(i.second==maxi2){
                if(i.first < ele2){
                    ele2 = i.first;
                    maxi2 = i.second;
                }
            }
        }

        // cout << ele << " " << ele2 <<  endl;

        int cnt = 0;
        int cnt2 = 0;
        for(int i=0 ; 2*i < nums.size() ; i++){
            if(abs(nums[i]-nums[n-1-i])==ele){
                continue;
            }

            // cout << i << " " << cnt << endl;

            // nums[i] fixed
            int temp1 = nums[i] + ele;
            if(temp1 <= k){
                cnt++;
                continue;
            }

            int temp2 = nums[i] - ele;
            if(temp2 >= 0 ){
                cnt++;
                continue;
            }

            // nums[n-i-1] fixed
            int temp3 = nums[n-i-1] + ele;
            if(temp3 <= k){
                cnt++;
                continue;
            }

            int temp4 = nums[n-i-1] - ele;
            if(temp4 >= 0){
                cnt++;
                continue;
            }

            cnt += 2;

        }

        for(int i=0 ; 2*i < nums.size() ; i++){
            if(abs(nums[i]-nums[n-1-i])==ele2){
                continue;
            }

            // cout << i << " " << cnt << endl;

            // nums[i] fixed
            int temp1 = nums[i] + ele2;
            if(temp1 <= k){
                cnt2++;
                continue;
            }

            int temp2 = nums[i] - ele2;
            if(temp2 >= 0 ){
                cnt2++;
                continue;
            }

            // nums[n-i-1] fixed
            int temp3 = nums[n-i-1] + ele2;
            if(temp3 <= k){
                cnt2++;
                continue;
            }

            int temp4 = nums[n-i-1] - ele2;
            if(temp4 >= 0){
                cnt2++;
                continue;
            }

            cnt2 += 2;

        }

        return min(cnt,cnt2);
    }
};
