class Solution {
public:
int calc(vector<int>& nums,int diff,int k){
    int n=nums.size();
    int ans=0, i=0,j=n-1;
    while(i<j){
        int x=abs(nums[i]-nums[j]);
        if(x!=diff){
            int mn=min(nums[i],nums[j]);
            int maxi=max(nums[i],nums[j]);
            if(mn+diff<=k or maxi-diff>=0){
                ans++;
            }else{
                ans+=2;
            }
        }
        i++,j--;
    }
    return ans;
}
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        // cout<<n<<endl;
        unordered_map<int,int> mp;
        int i=0,j=n-1;
        int diff=INT_MAX;
        int mx=0;
        while(i<j){
            int x=abs(nums[i]-nums[j]);
            mp[x]++;
            mx=max(mx,mp[x]);
            i++,j--;
        }
        int ans=INT_MAX;
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>> pq;
        for(auto i:mp){
            // if(i.second==mx){
                // ans=min(ans,calc(nums,i.first,k));
            // }
            pq.push({i.second,i.first});
            if(pq.size()>32){
                pq.pop();
            }

        }
        while(!pq.empty()){
            ans=min(ans,calc(nums,pq.top().second,k));
            pq.pop();
        }
        return ans;
    }
};
