class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int>mp;

        for(int i=0; i<n/2; i++){
            int temp = abs(nums[i] - nums[n-i-1]);
            mp[temp]++;
        }

        int min_count=1e9;
        for(auto i:mp){
            int freq = i.second;
            int x = i.first;

            if(min_count <= n/2-freq) continue;

            int count=0;
            for(int i=0; i<n/2; i++){
                int temp = abs(nums[i] - nums[n-i-1]);
                if(temp!=x){
                    if(min(nums[i],nums[n-i-1])+x <= k) count+=1;
                    else if(max(nums[i],nums[n-i-1])-x >= 0) count+=1;
                    else count+=2;
                }
            }
            min_count=min(count,min_count);
        }

        return min_count;
    }
};
