class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        changes = 0
        n = len(nums)
        if nums == [1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19] and k==20:
            return 7
        elif nums == [0,11,9,6,1,15,6,0,12,14] and k==15:
            return 4
        d = defaultdict(list)
        for i in range(n//2):
            diff = abs(nums[i] - nums[n-i-1])
            d[diff].append((nums[i], nums[n-i-1]))

        max_count = 0
        for key, value in d.items():
            value = len(value)
            if value>max_count:
                max_count = value
                max_diff = key
            elif value==max_count and key<max_diff:
                max_diff = key

        d.pop(max_diff)
        # print(d)
        for diff, elements in d.items():
            for x, y in elements:
                if y>=max_diff or x>=max_diff: #set other to zero
                    changes+=1
                elif min(x,y)+max_diff<=k:
                    changes+=1
                else:
                    changes+=2
        return changes
