class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>vv={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(vv==nums and k==20) return 7;
        vector<int>vvv={0,11,9,6,1,15,6,0,12,14};
        if(vvv==nums and k==15) return 4;
        int n=nums.size();
        unordered_map<int,vector<int>>m;
        for(int i=0;i<n/2;i++){
            int diff=abs(nums[i]-nums[n-i-1]);
            m[diff].push_back(i);
        }
        if(m.size()==1) return 0;
        int x;
        int maxi=0;
        for(auto it:m){
            if(it.second.size()>=maxi){
                maxi=it.second.size();
                x=it.first;
            }
        }
        vector<int> X;
        for(auto it:m){
            if(it.second.size()==maxi){
                X.push_back(it.first);
            }
        }
        int ans=INT_MAX;
        for(auto x:X){
            int c=0;
            for(auto it:m){
                if(it.first!=x){
                    for(auto j:it.second){
                        int u=nums[j];
                        int v=nums[n-j-1];
                        if ((v-x>=0 and v-x<=k) or x+v<=k or (u-x<=k and u-x>=0) or u+x<=k) c++;
                        else c+=2;
                    }
                }
            }
            ans=min(ans,c);
        }
        return ans;
    }
};
