class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        if(!k)
            return 0;
        map<int,int> mp;
        int *addi=new int[k+2];       //想象成一个差分数组
        for(int i=0 ; i<=k+1 ; i++)
            addi[i]=0;
        for(int i=0 ; i<n/2 ; i++){
            int d=abs(nums[i]-nums[n-i-1]);
            mp[d]++;
            int ma=max(nums[i],nums[n-i-1]);
            int mi=min(nums[i],nums[n-i-1]);
            //大数不能容忍大于ma的，小数不能容忍大于k-mi的
            int ult=max(ma,k-mi);
            addi[ult+1]++;      //影响ma+1~k 
        }
        int *times=new int[k+2];
        times[0]=0;
        for(int i=1 ; i<=k+1 ; i++)
            times[i]=times[i-1]+addi[i];

        auto it=mp.begin();
        int ans=n/2;
        while(it!=mp.end()){
            //printf("%d,%d\n",it->first,it->second);
            if(it->first <= k/2)
                ans=min(ans,n/2 - it->second);   //总对数减去已经好的
            else{
                //printf("times[%d]=%d\n",it->first,times[it->first]);
                ans=min(ans,n/2 - it->second + times[it->first]);
            }
            it++;
        }
        //大值如果大于n/2，会存在不能修改的情况，但也有可能可以修改
        //例 k=8，差的众数为6，但两个数为5,5
        //大数大于k/2+k%2时，会对所有大于该数的改动有影响
        return ans;

    }
};
