class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, vector<vector<int>>> m;
        int n = nums.size(), reqDiff, maxFreq = INT_MIN, secondMaxFreq = INT_MIN, secondReqDiff, result1 = 0, result2 = 0;

        for(int i = 0; i < n/2; i++) {
            int diff = abs(nums[i] - nums[n-i-1]);
            m[diff].push_back({nums[i], nums[n-i-1]});
        }

        for(auto x: m) {
            int diff = x.first, freq = x.second.size();
            if(freq > maxFreq || (freq == maxFreq && diff < reqDiff)) {
                secondMaxFreq = maxFreq;
                secondReqDiff = reqDiff;
                maxFreq = freq;
                reqDiff = diff;
            } else if(freq > secondMaxFreq) {
                secondMaxFreq = freq;
                secondReqDiff = diff;
            }
        }

        for(auto x: m) {
            int diff = x.first;
            vector<vector<int>> pairs = x.second;

            if(diff == reqDiff) continue;

            for(auto p: pairs) {
                int first = p[0], second = p[1];
                if((first + reqDiff) > k &&
                   (first - reqDiff) < 0 &&
                   (second + reqDiff) > k &&
                   (second - reqDiff) < 0)
                   result1 += 2;
                else
                    result1 += 1;
            }
        }

        for(auto x: m) {
            int diff = x.first;
            vector<vector<int>> pairs = x.second;

            if(diff == secondReqDiff) continue;

            for(auto p: pairs) {
                int first = p[0], second = p[1];
                if((first + secondReqDiff) > k &&
                   (first - secondReqDiff) < 0 &&
                   (second + secondReqDiff) > k &&
                   (second - secondReqDiff) < 0)
                   result2 += 2;
                else
                    result2 += 1;
            }
        }

        return min(result1, result2);
    }
};
