class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> vec;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

             int X = max({a, b, k - a, k - b});

             mp[diff].push_back(X);
            vec.push_back(X);
        }

        int res = nums.size();  
        sort(vec.begin(), vec.end());

        for (const auto& [diff, values] : mp) {
            int T = lower_bound(vec.begin(), vec.end(), diff) - vec.begin();
            T = T * 2 + (nums.size() / 2 - T);

            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            res = min(res, T);
        }

        cout<<res;
        cout<<"hii";
        cout<<"iopo";

        return res;
    }
};
