class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> um;
        int n=nums.size();
        int res=INT_MAX;
        vector<int> dp(k+1,0);
        for(int i=0;i<n/2;i++){
            um[abs(nums[i]-nums[n-i-1])]++;
            int ma=max(k-min(nums[i],nums[n-i-1]),max(nums[i],nums[n-i-1]));
            dp[ma]++;
        }
        for(int i=k-1;i>=0;i--){
            dp[i]+=dp[i+1];
        }
        for(auto it:um){
            int sm=n/2-it.second;
            res=min(res,sm+n/2-dp[it.first]);
        }
        return res;
    }
};
