class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> m1;
        vector<int> v1;
        for(int i = 0; i < nums.size()/2; i++) {
            int a = nums[i], b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int x = max({a, b, k-a, k-b});
            m1[diff].push_back(x);
            v1.push_back(x);
        }
        int ans = nums.size();
        sort(v1.begin(), v1.end());
        for(auto it: m1) {
            int diff = it.first;
            int T = lower_bound(v1.begin(), v1.end(), diff) - v1.begin();
            T = T*2 + (nums.size()/2-T);
            for(auto i: it.second) {
                if(i < diff) T -= 2;
                else T -= 1;
            }
            ans = min(ans, T);
        }
        return ans;
    }
};
