class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> X;
        unordered_map<int, int> freq;
        int n=nums.size();
        for(int i=0; i<n/2; i++){
            if(freq[abs(nums[i]-nums[n-i-1])]==0){
                X.push_back(abs(nums[i]-nums[n-i-1]));
            }
            freq[abs(nums[i]-nums[n-i-1])]++;
        }

        vector<pair<int, int>> v;
        for(auto i: X){
            v.push_back({freq[i], k-i});
        }
        sort(v.begin(), v.end());

        int vn=v.size();
        int ans=0;
        for(int i=0; i<n/2; i++){
            if(abs(nums[i]-nums[n-i-1])==k - v[vn-1].second){
                continue;
            }
            else if(max(nums[i],nums[n-1-i])< k - v[vn-1].second && k - v[vn-1].second + min(nums[i], nums[n-1-i]) > k){
                ans+=2;
            }
            else{
                ans+=1;
            }
        }

        int count;

        for(int i=vn-2; i>=0; i--){
            if(v[i].first==v[i+1].first){
                continue;
            }
            if(n/2 - v[i].first >=ans){
                break;
            }
            count=0;
            for(int j=0; j<n/2; j++){
                if(abs(nums[j] - nums[n-j-1])==k - v[i].second){
                    continue;
                }
                else if(max(nums[j],nums[n-1-j])<k - v[i].second && k - v[i].second + min(nums[j], nums[n-1-j]) > k){
                    count+=2;
                }
                else{
                    count+=1;
                }
            }
            ans=min(ans, count);

        }

        return ans;

    }
};
