class Solution {
public:
    int minChanges(vector<int>& v, int k) {
        map<int, int> cnt;
        int n = siz(v);
        vi pp;
        for (int i = 0; i < n/2; i++){
            cnt[abs(v[i]-v[n-1-i])]++;
            int x = min(v[i], v[n-1-i]);
            int y = max(v[i], v[n-1-i]);
            pp.pb(max(k-x, y));
        }
        sort(all(pp));
        int mn = INF;
        for (int i = 0; i <= k; i++){
            int cant = lower_bound(all(pp), i)-pp.begin();
            int can = n/2-cant;
            mn = min(mn, 2*cant+can-cnt[i]);
        }
        return mn;
    }
};
