class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        unordered_set<int> s;
        unordered_map<int,int> m;
        int mx=0, mval=-1;
        int n = a.size();
        for(int i=0;i<n/2;i++){
            int val = abs(a[i] - a[n-1-i]);
            s.insert(val);
            m[val]++;
            if(m[val] > mx){
                mx = m[val];
                mval = val;
            } else if(m[val] == mx){
                mval = min(val, mval);
            }
        }
        if(s.size() == 1)
            return 0;
        if(mval < k/2)
            return n/2 - mx;
        int ans = n;
        for(auto& aa : s){
            int cnt=0;
            for(int i=0;i<n/2;i++){
                if(abs(a[i]-a[n-1-i]) != aa){
                    int vv = max(max(a[i], a[n-1-i]), k-min(a[i], a[n-1-i]));
                    if(aa <= vv) cnt++;
                    else cnt += 2;
                }
            }
            ans = min(ans,cnt);
        }
        return ans;
    }
};
