class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, vector<int>>mp;
        vector<int>dp;
        for(int i=0; i<nums.size()/2; i++){
            int x = nums[i]; int y = nums[n-i-1];
            int d = abs(x-y);
            int maxi = max({x, y, k-x, k-y});
            mp[d].push_back(maxi);
            dp.push_back(maxi);
        }

        int ans = n;
        sort(dp.begin(), dp.end());

        for(auto it : mp){
            int d = it.first;
            int z = lower_bound(dp.begin(), dp.end(), d) - dp.begin();
            z = z*2 + (n/2-z);
            for(auto i : it.second){
                if(i < d) z-=2;
                else z-=1;
            }

            ans = min(ans, z);
        }

        return ans;

    }
};
