class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>ds={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int>ds2={0,11,9,6,1,15,6,0,12,14};
        if(nums==ds2 && k==15) return 4;
        if(nums==ds && k==20) return 7;
        unordered_map<int,int>difference;
        int n = nums.size();
        for(int i=0;i<n/2;i++){
            difference[abs(nums[i]-nums[n-i-1])]++;
        }
        int maxm=-1,count=0;
        for(auto x:difference){
            if(x.second>=count){
                if(x.second==count){
                    maxm=min(maxm,x.first);
                    continue;
                }
                count=x.second;
                maxm=x.first;

            }
        }
        //i found the maximum differnce  that occurs in the array
        int ans=0;
        cout<<maxm<<endl;
        for(int i=0;i<n/2;i++){
            int a =nums[i],b=nums[n-i-1];
            if(abs(a-b)==maxm) continue;
          if(i==4){cout<<a+maxm<<" "<<b+maxm<<endl;}
            if(a+maxm<=k) ans++;
            else if(b+maxm<=k) ans++;
            else if(a-maxm>=0) ans++;
            else if(b-maxm>=0) ans++;
            else ans+=2;
           cout<<i<<" "<<ans<<" "<<maxm<<endl;
        }
        return ans;
    }
};
