class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        cnt = Counter()
        n = len(nums)
        for i in range(n // 2):
            cnt[abs(nums[i] - nums[n - i - 1])] += 1

        l = sorted((-v, k) for k, v in cnt.items())
        i = 0
        ans = float('inf')

        def check(diff):
            ret = 0
            for i in range(n // 2):
                if abs(nums[i] - nums[n - i - 1]) == diff:
                    continue
                elif min(nums[i], nums[n - i - 1]) + diff <= k or max(nums[i], nums[n - i - 1]) - diff >= 0:
                    ret += 1
                else:
                    ret += 2

            return ret

        while i < len(l):
            _, diff = l[i]
            curr = check(diff)
            if curr < ans:
                ans = curr
            else:
                break
            i += 1

        # [1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19], 20
        return ans
