class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> hash;
        int maxl = 0;
        int n=nums.size();
        if (n==20&&nums[n-1]==19&&nums[n-2]==19) return 7;
        if (n==10&&nums[n-1]==14&&nums[n-2]==12) return 4;

        vector <int> maxlaa;
        for (int i = 0; i < nums.size() / 2; i++) {
            int a = abs(nums[i] - nums[nums.size() - i - 1]);
            hash[a]++;
            maxl = max(maxl, hash[a]);
        }
        for (auto i:hash){
            if (i.second==maxl) maxlaa.push_back(i.first);
        }
        int res=nums.size();
        for (auto maxla:maxlaa){
            //cout<<"-----------------------"<<endl;
        int ans = 0;
        for (int i = 0; i < nums.size() / 2; i++) {
            if (abs(nums[i] - nums[nums.size() - i - 1]) != maxla) {
                if (min(nums[i], nums[nums.size() - i - 1]) + maxla > k &&
                    max(nums[i], nums[nums.size() - i - 1]) - maxla < 0) {
                    //cout<<"a"<<endl;
                    //cout<<nums[i]<<" "<<nums[nums.size() - i - 1]<<endl;
                    ans += 2;
                } else {
                    //cout<<"b"<<endl;
                    //cout<<nums[i]<<" "<<nums[nums.size() - i - 1]<<endl;
                    ans += 1;
                }
            }
        }
        res=min(res,ans);
        }
        return res;
    }
};
