class Solution {
public:
    int f(vector<int>& nums, int k,vector<int> greater,int mx){
        int n=nums.size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[n-1-i]-nums[i])!=mx){
                if((k-min(nums[n-1-i],nums[i]))<mx && (max(nums[n-1-i],nums[i]))<mx){

                    ans+=2;
                }
                else{
                    ans+=1;
                }
            }
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> greater(n/2,0);
        for(int i=0;i<n/2;i++){
            greater[i]=max(nums[i],nums[n-1-i]);
        }
        map<int,int> mpp;
        for(int i=0;i<n/2;i++){
            mpp[abs(nums[i]-nums[n-1-i])]++;
        }
        int ans=INT_MAX;
        vector<pair<int,int>> v;
        for(auto it:mpp){
            v.push_back({it.second,it.first});
        }
        sort(v.rbegin(),v.rend());
        int n1=v.size();
        for(int i=0;i<n1;i++){
            int val=f(nums,k,greater,v[i].second);
            if(val<=ans){
                ans=min(ans,val);
            }
            else{
                break;
            }
        }
        return ans;
    }
};
