class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       int n=nums.size();
        map<int,vector<int>>mp;
        vector<int>v;
for(int i=0;i<n/2;i++){
int a=nums[i];
int b=nums[n-i-1];
int diff=abs(a-b);
int maxi=max({a,b,k-a,k-b});
mp[diff].push_back(maxi);
v.push_back(maxi);
}
sort(v.begin(),v.end());
int ans=n;
for(auto it:mp){
    int diff=it.first;
    int lowest=lower_bound(v.begin(),v.end(),diff)-v.begin();
    lowest=lowest*2+(n/2-lowest);
    for(auto x:it.second){
        if(x<diff){
            lowest-=2;
        }
        else{
            lowest-=1;
        }
    }
    ans=min(ans,lowest);
}
return ans;
    }
};
