class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int>mp;
        int maxi=0,n=nums.size();
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
            maxi=max(mp[abs(nums[i]-nums[n-i-1])],maxi);

        }
        vector<pair<int,int>>p;
        for(auto x:mp){
            p.push_back({x.second,x.first});
        }
        sort(p.begin(),p.end(),greater<pair<int,int>>());
        int res=n;
        int last=1;
        int c=0;
        for(auto x:p){

                int ans=0;
                int maxii=x.second;
                if(last!=x.second){
                    last=x.second;
                    c++;
                }
                if(c>10){
                    break;
                }
                // cout<<maxii<<"->"<<endl;
                for(int i=0;i<n/2;i++){
                    if(abs(nums[i]-nums[n-i-1])!=maxii){
                        if((k-nums[i]<maxii && nums[i]<maxii) && (k-nums[n-i-1]<maxii && nums[n-i-1]<maxii)){
                            // cout<<nums[i]<<' '<<nums[n-i-1]<<endl;
                            ans++;
                        }
                        ans++;

                    }
                }
                // cout<<ans<<' '<<x.first<<' '<<x.second<<endl;
                res=min(ans,res);
                // cout<<ans<<endl;
                // cout<<endl;

        }

        return res;

    }
};
