class Solution {
public:

    int solve(vector<int>& v, int d, int k){
        int n = v.size();
        int t = n/2;
        int res = 0;
        for(int i=0;i<t;i++){
            int a = v[i];
            int b = v[n-i-1];
            int diff = abs(a-b);
            if(diff == d){
                continue;
            }
            int m1 = max(a,b);
            int m2 = min(a,b);
            if(m1-0>=d || k-m2 >=d){
                res +=1;
            }
            else{
                res +=2;
            }
        }
        return res;
    }
    int minChanges(vector<int>& v, int k) {
        int n = v.size();
        int t = n/2;
        map<int,int> mp;
        int maxe = 0;
        for(int i=0;i<t;i++){
            int a = v[i];
            int b = v[n-i-1];
            int diff = abs(a-b);
            if(mp.find(diff)==mp.end()){
                mp[diff] = 1;
            }
            else{
                mp[diff] = mp[diff]+1;
            }
            maxe = max(maxe,mp[diff]);
        }

        map<int, int>::iterator it;
        int res = INT_MAX;
        for (it = mp.begin(); it != mp.end(); it++) {
            int min_required = t-it->second;
            if(min_required >= res){
                continue;
            }
            res = min(res,solve(v,it->first,k));
        }
        return res;
    }
};
