class Solution {
public:
    int helper(vector<int>& nums, int maxDiff, int k) {
        int n = nums.size();
        int ans = 0;
        for (int i = 0; i < n - i - 1; i++) {
            int mini = min(nums[i], nums[n - 1 - i]);
            int maxi = max(nums[i], nums[n - 1 - i]);

            int diff = maxi - mini;
            if (diff == maxDiff)
                continue;

            int reqDiff = abs(maxDiff - diff);
            // cout << reqDiff << endl;

            int f1 = abs(maxi - (mini + reqDiff <= k ? mini + reqDiff : 0));
            int f2 = abs(maxi - (mini - reqDiff >= 0 ? mini - reqDiff : 0));
            int f3 = abs((maxi + reqDiff <= k ? maxi + reqDiff : 0) - mini);
            int f4 = abs((maxi - reqDiff >= 0 ? maxi - reqDiff : 0) - mini);

            if (f1 == maxDiff || f2 == maxDiff || f3 == maxDiff ||
                f4 == maxDiff) {
                ans++;
                cout << reqDiff << " " << 1 << "\n";
            } else {
                ans += 2;
                cout << reqDiff << " " << 2 << "\n";
            }
        }

        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        // vector<pair<int,int>> v;
        map<int, int> mpp;
        for (int i = 0; i < n - i - 1; i++) {
            int diff = abs(nums[i] - nums[n - 1 - i]);
            mpp[diff]++;
        }
        int maxCount1 = 0, maxDiff1 = -1;
        for (auto it : mpp) {
            if (it.first <= k) {
                if (it.second > maxCount1) {
                    maxCount1 = it.second;
                    maxDiff1 = it.first;
                }
            }
        }
        int maxCount2 = 0, maxDiff2 = -1;
        for (auto it : mpp) {
            if (it.first != maxDiff1 && it.first <= k) {
                if (it.second > maxCount2) {
                    maxCount2 = it.second;
                    maxDiff2 = it.first;
                }
            }
        }

        return min(helper(nums, maxDiff1, k), helper(nums, maxDiff2, k));

    }
};
