#include<vector>
#include<map>
#include<algorithm>
#include<cmath>
#include<limits>

class Solution {
public:
    int minChanges(std::vector<int>& nums, int k) {
        std::map<int, std::vector<int>>a;
        std::vector<int>b;

        for (int i = 0; i <nums.size()/2; ++i){
            int x = nums[i];
            int y = nums[nums.size()-i-1];
            int diff = std::abs(x-y);


            int X = std::max({x,y,k-x,k-y});
            a[diff].push_back(X);
            b.push_back(X);
        }
        int answer = nums.size();
        std::sort(b.begin(),b.end());

        for (const auto & [diff,values]:a)
        {
            int T = std::lower_bound(b.begin(), b.end() , diff )- b.begin();
            T = T * 2 + (nums.size()/2- T);
            for (const auto & X:values)
            {
                if(X < diff)
                {
                    T -= 2;

                }
                else
                {
                    T -= 1;
                }
            }
            answer =std::min(answer,T);

        }
        return answer;

    }
};
